import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  add,
  update,
  remove,
  removeCompleted,
  reorderArray,
} from "../../store/reducers/todo.reducer";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Checkbox } from "../../components/CheckBox/CheckBox";
import { Input } from "../../components/Input/Input";
import { Button } from "../../components/Button/Button";
import "./MainPage.css";

const initialTodo = {
  id: "",
  name: "",
  completed: false,
};

const itemsTodo = [
  { id: "dqwds", title: "do something" },
  { id: "zxczxc", title: "do something now" },
  { id: "bxcvxc", title: "do something later" },
];

export const MainPage = () => {
    const todos = useSelector((state) => state.todo);
    const dispatch = useDispatch();
    const [todo, setTodo] = useState(initialTodo);
    const [selected, setSelected] = useState("");

    // Function to update list on drop
    const handleDrop = (droppedItem) => {
      // Ignore drop outside droppable container
      if (!droppedItem.destination) return;
      var updatedList = [...todos];
      // Remove dragged item
      const [reorderedItem] = updatedList.splice(droppedItem.source.index, 1);
      // Add dropped item
      updatedList.splice(droppedItem.destination.index, 0, reorderedItem);
      // Update State
      dispatch(reorderArray(updatedList));
    };

    const sortedItems = sortTodos(todos, selected);

    const handleUpdate = (value) => {
      dispatch(update(value));
    };

    const handleRemove = (value) => {
      dispatch(remove(value));
    };

    const handleRemoveCompleted = () => {
      dispatch(removeCompleted());
    };

    const handleChange = (event) => {
      setTodo({
        ...todo,
        name: event.target.value,
      });
    };

    const handleCheckbox = (event) => {
      setTodo({
        ...todo,
        completed: event.target.checked,
      });
    };

    const handleKey = (event) => {
      if (event.keyCode === 13 && todo.name.length > 0) {
        dispatch(
          add({
            id: crypto.randomUUID(),
            name: todo.name,
            completed: todo.completed,
          })
        );
        setTodo(initialTodo);
      }
    };


  return (
    <div className="todo-page">
      <div className="input-todo-container">
        <input className="input-style" type="text" placeholder="Add" />
      </div>
      <div className="list-todo-container">
        <DragDropContext onDragEnd={handleDrop}>
          <Droppable droppableId="todo-list-container">
            {(provided) => (
              <div
                className="todo-list-container"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {itemList.map((item, index) => (
                  <Draggable
                    key={item.title + item.id}
                    draggableId={item.id}
                    index={index}
                  >
                    {(provided) => (
                      <div
                        className="todo-item-container"
                        ref={provided.innerRef}
                        {...provided.dragHandleProps}
                        {...provided.draggableProps}
                      >
                        <label
                          style={{ display: "flex", alignItems: "center" }}
                        >
                          <input type="checkbox" className="checkbox-style" />
                          <span style={{ marginLeft: "10px" }}>
                            {item.title}
                          </span>
                        </label>
                        <button
                          style={{
                            border: "none",
                            backgroundColor: "white",
                            cursor: "pointer",
                          }}
                        >
                          X
                        </button>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
        <div className="list-footer"></div>
      </div>
      <div className="footer-todo-container">drag items</div>
    </div>
  );
};
