import { configureStore } from "@reduxjs/toolkit";

const initialState = {
  items: [
    { id: "1", title: "Do homework" },
    { id: "2", title: "Pay rent" },
    { id: "3", title: "Call mom" },
  ],
};

const todoReducer = (state = initialState, action) => {
  // Handle actions here
  return state;
};

const store = configureStore({
  reducer: {
    todos: todoReducer,
  },
});

export default store;
