import { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./todo.css";

const itemsTodo = [
    { id: 'dqwds', title: "do something else now" },
    { id: 'zxczxc', title: "do something now" },
    { id: 'bxcvxc', title: "do something later" },
  ]

export const Todo = () => {
  const [itemList, setItemList] = useState(itemsTodo);

  const handleDrop = (droppedItem) => {
    // Ignore drop outside droppable container
    if (!droppedItem.destination) return;
    var updatedList = [...itemList];
    // Remove dragged item
    const [reorderedItem] = updatedList.splice(droppedItem.source.index, 1);
    // Add dropped item
    updatedList.splice(droppedItem.destination.index, 0, reorderedItem);
    // Update State
    setItemList(updatedList);
  };

  return (
    <div className="todo-page">
      <div className="input-todo-container">
        <input className="input-style" type="text" placeholder="Add" />
      </div>
      <div className="list-todo-container">
        <DragDropContext onDragEnd={handleDrop}>
          <Droppable droppableId="todo-list-container">
            {(provided) => (
              <div
                className="todo-list-container"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {itemList.map((item, index) => (
                  <Draggable
                    key={item.title + item.id}
                    draggableId={item.id}
                    index={index}
                  >
                    {(provided) => (
                      <div
                        className="todo-item-container"
                        ref={provided.innerRef}
                        {...provided.dragHandleProps}
                        {...provided.draggableProps}
                      >
                        <label style={{display: 'flex', alignItems: 'center'}}>
                        <input type="checkbox" className="checkbox-style" />
                        <span style={{marginLeft: '10px'}}>{item.title}</span>
                        </label>
                        <button style={{border: 'none', backgroundColor: 'white', cursor: 'pointer'}}>X</button>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
        <div className="list-footer"></div>
      </div>
      <div className="footer-todo-container">drag items</div>
    </div>
  );
};
